pragma solidity ^0.5.4;

import "openzeppelin-solidity/contracts/ownership/Ownable.sol";
import "openzeppelin-solidity/contracts/math/SafeMath.sol";

/// @author The CoinChallenges Team
/// @title CoinChallenges Main Contract
contract CoinChallenges is Ownable {
    using SafeMath for uint256;

    uint8 public constant MinDays = 12;
    uint8 public constant MaxDays = 90;

    enum ChallengeStatus { Succeeded, Failed, Aborted }

    struct ChallengeGroup {
        uint256 id;
        string name;
        string url;
        uint8 minDays;
        uint8 maxDays;
        uint8 maxDelayDays;
        uint256 minAmount;
        uint256 balance;
        address owner;
        address agent;
    }

    struct Challenge {
        address who;
        uint256 groupId;
        string goal;
        uint8 targetDays;
        uint8 totalDays;
        uint8 completeDays;
        uint256 startTimestamp;
        uint256 amount;
        uint256 sponsorSize;
        uint256 totalSponsorAmount;
        mapping (uint256 => Sponsor) sponsors;
        mapping (uint256 => bool) dayIsCompletes; // index is from 0 to (totalDays - 1)
    }

    struct Sponsor {
        address who;
        uint256 amount;
        string comment;
    }

    /// the balance of contract owner
    uint256 public ownerBalance;

    /// address to name
    mapping (address => string) public names;

    /// id to ChallengeGroup
    mapping (uint256 => ChallengeGroup) challengeGroups;

    /// groupId to address to round to challenge
    mapping (uint256 => mapping (address => mapping (uint256 => Challenge))) challenges;

    /// groupId to address to current round
    mapping (uint256 => mapping (address => uint256)) currentRounds;

    /// address to user's balance
    mapping (address => uint256) public userBalances;

    event NewChallengeGroup(address indexed owner, uint256 id);

    event NewChallenge(
        uint256 indexed groupId,
        address indexed challenger,
        uint256 indexed round,
        string goal,
        uint8 targetDays,
        uint8 totalDays,
        uint256 startTimestamp,
        uint256 amount);

    event FinishChallenge(
        uint256 indexed groupId,
        address indexed challenger,
        uint256 indexed round,
        string goal,
        uint8 targetDays,
        uint8 totalDays,
        uint8 completeDays,
        uint256 startTimestamp,
        uint256 amount,
        uint256 sponsorSize,
        uint256 totalSponsorAmount,
        uint8 status);

    event SponsorChallenge(
        address sponsor,
        uint256 indexed groupId,
        address indexed challenger,
        uint256 indexed round,
        uint256 amount,
        string comment);

    event SponsorWin(
        address indexed who,
        uint256 indexed groupId,
        address indexed challenger,
        uint256 round,
        uint256 amount);

    constructor() public {
    }

    /// only challenger and agent
    /// @dev challenger or group.agent
    /// @param _groupId group.id
    /// @param _who challenge.who
    modifier onlyChallengeOwnerAndGroupAgent(uint256 _groupId, address _who) {
        ChallengeGroup memory group = _getChallengeGroup(_groupId);
        require(msg.sender == group.agent || msg.sender == _who, "permission: user/agent");
        _;
    }

    /// update address name
    /// @param _name the name of address
    function updateName(string memory _name) public {
        names[msg.sender] = _name;
    }

    /// create challenge group
    /// @param _id any unsigned integer but must greater than 0 and no one use it before
    /// @param _name group name
    /// @param _url group image url
    /// @param _minDays min days for challenge. Must greater than or equal to global minDays
    /// @param _maxDays max days for challenge. Must less than or equal to global maxDays
    /// @param _maxDelayDays max delay days for challenge to complete daily goal
    /// @param _minAmount min amount for challenge and sponsor
    /// @param _agent agent address to help sending complete days
    function createChallengeGroup(
        uint256 _id,
        string memory _name,
        string memory _url,
        uint8 _minDays,
        uint8 _maxDays,
        uint8 _maxDelayDays,
        uint256 _minAmount,
        address _agent
    ) public {
        require(_id > 0, "must greater than 0");
        require(challengeGroups[_id].minDays == 0, "duplicate id");
        require(_minDays >= MinDays, "must greater than or equal to global minDays");
        require(_maxDays <= MaxDays, "must less than or equal to global maxDays");
        require(_maxDelayDays <= MaxDays, "must less than or equal to global maxDays");

        challengeGroups[_id] = ChallengeGroup(_id, _name, _url, _minDays, _maxDays, _maxDelayDays, _minAmount, 0, msg.sender, _agent);
        emit NewChallengeGroup(msg.sender, _id);
    }

    /// get challenge group
    /// @param _id group id
    /// @return challenge group properties
    function getChallengeGroup(uint256 _id) public view returns (
        string memory _name,
        string memory _url,
        uint8 _minDays,
        uint8 _maxDays,
        uint8 _maxDelayDays,
        uint256 _minAmount,
        uint256 _balance,
        address _owner,
        address _agent
    ) {
        ChallengeGroup memory group = _getChallengeGroup(_id);

        _name = group.name;
        _url = group.url;
        _minDays = group.minDays;
        _maxDays = group.maxDays;
        _maxDelayDays = group.maxDelayDays;
        _minAmount = group.minAmount;
        _balance = group.balance;
        _owner = group.owner;
        _agent = group.agent;
    }

    /// modify challenge group
    /// @dev you can only modify name, url and minAmount
    function modifyChallengeGroup(uint256 _id, string memory _name, string memory _url, uint256 _minAmount) public {
        ChallengeGroup storage group = challengeGroups[_id];
        require(group.owner == msg.sender, "permission: group owner");

        group.name = _name;
        group.url = _url;
        group.minAmount = _minAmount;
    }

    /// create challenge
    /// @param _groupId group id
    /// @param _goal challenge goal for frontend app to determine whether to complete the goal
    /// @param _targetDays target days to determine if the challenge is successful
    /// @param _totalDays challenge total days
    /// @param _startTimestamp start timestamp (seconds)
    function createChallenge(
        uint256 _groupId,
        string memory _goal,
        uint8 _targetDays,
        uint8 _totalDays,
        uint256 _startTimestamp
    ) public payable {
        require(_startTimestamp > now - 1 days, "must greater than now - 1 day");

        ChallengeGroup memory group = _getChallengeGroup(_groupId);
        require(_totalDays >= group.minDays, "must greater than or equal to group's minDays");
        require(_totalDays <= group.maxDays, "must less than or equal to group's maxDays");
        require(_targetDays >= group.minDays, "must greater than or equal to group's minDays");
        require(_targetDays <= _totalDays, "must less than or equal to totalDays");
        require(msg.value >= group.minAmount, "must greater than group's minAmount");

        uint256 currentRound = currentRounds[_groupId][msg.sender];
        require(challenges[_groupId][msg.sender][currentRound].totalDays == 0, "duplicate challenge");

        if (currentRound == 0) {
            currentRound = 1;
            currentRounds[_groupId][msg.sender] = 1;
        }

        challenges[_groupId][msg.sender][currentRound] = Challenge(msg.sender, _groupId, _goal, _targetDays, _totalDays, 0, _startTimestamp, msg.value, 0, 0);
        emit NewChallenge(_groupId, msg.sender, currentRound, _goal, _targetDays, _totalDays, _startTimestamp, msg.value);
    }

    /// get current challenge
    /// @dev get the newest round of challenge
    /// @param _groupId group id
    /// @param _address challenger address
    /// @return challenge properties
    function getCurrentChallenge(
        uint256 _groupId,
        address _address
    ) public view returns (
        uint256 _currentRound,
        string memory _goal,
        uint8 _targetDays,
        uint8 _totalDays,
        uint8 _completeDays,
        uint256 _startTimestamp,
        uint256 _amount,
        uint256 _sponsorSize,
        uint256 _totalSponsorAmount,
        uint256[] memory _completeTimestamps
    ) {
        uint256 currentRound = currentRounds[_groupId][_address];
        return getChallenge(_groupId, _address, currentRound);
    }

    /// get challenge
    /// @param _groupId group id
    /// @param _address challenger address
    /// @param _round round number
    /// @return challenge properties
    function getChallenge(
        uint256 _groupId,
        address _address,
        uint256 _round
    ) public view returns (
        uint256 _currentRound,
        string memory _goal,
        uint8 _targetDays,
        uint8 _totalDays,
        uint8 _completeDays,
        uint256 _startTimestamp,
        uint256 _amount,
        uint256 _sponsorSize,
        uint256 _totalSponsorAmount,
        uint256[] memory _completeTimestamps
    ) {
        Challenge storage challenge = challenges[_groupId][_address][_round];

        // setup _completeTimestamps values
        _completeTimestamps = new uint256[](challenge.completeDays);
        uint8 index = 0;
        for (uint dayIndex = 0; dayIndex < challenge.totalDays; dayIndex++) {
            if (challenge.dayIsCompletes[dayIndex] == true) {
                _completeTimestamps[index] = challenge.startTimestamp.add(dayIndex * 1 days);
                index++;
            }
        }

        _currentRound = _round;
        _goal = challenge.goal;
        _targetDays = challenge.targetDays;
        _totalDays = challenge.totalDays;
        _completeDays = challenge.completeDays;
        _startTimestamp = challenge.startTimestamp;
        _amount = challenge.amount;
        _sponsorSize = challenge.sponsorSize;
        _totalSponsorAmount = challenge.totalSponsorAmount;
    }

    /// get sponsor
    /// @param _groupId group id
    /// @param _address challenger address
    /// @param _round round number
    /// @param _index sponsor index
    /// @return sponsor properties
    function getSponsor(
        uint256 _groupId,
        address _address,
        uint256 _round,
        uint256 _index
    ) public view returns (
        address _who,
        uint256 _amount,
        string memory _comment
    ) {
        require(_index < challenges[_groupId][_address][_round].sponsorSize, "must less than sponsor size");
        Sponsor memory sponsor = challenges[_groupId][_address][_round].sponsors[_index];
        return (sponsor.who, sponsor.amount, sponsor.comment);
    }

    /// Complete daily challenge
    /// @dev complete multiple daily challenges. Only challenger and group agent can run.
    /// @param _groupId group id
    /// @param _who challenger address
    /// @param timestamps timestamp array to record challenge.dayIsCompletes
    function completeDailyChallenges(uint256 _groupId, address _who, uint256[] memory timestamps)
        public onlyChallengeOwnerAndGroupAgent(_groupId, _who) {

        ChallengeGroup memory group = _getChallengeGroup(_groupId);
        (, Challenge storage challenge) = _getCurrentChallenge(_groupId, _who);

        for (uint256 index = 0; index < timestamps.length; index++) {
            uint256 timestamp = timestamps[index];
            uint256 dayIndex = timestamp.sub(challenge.startTimestamp) / 1 days;
            if (challenge.dayIsCompletes[dayIndex] == true) {
                continue;
            }
            require(timestamp < challenge.startTimestamp.add(challenge.totalDays * 1 days), "must less than endDay");
            require(timestamp <= now, "must less than or equal to current timestamp");

            uint256 fromTimestamp = now.sub(group.maxDelayDays * 1 days);
            require(timestamp >= fromTimestamp, "must greater than or equal to now - maxDelayDays");

            challenge.completeDays++;
            challenge.dayIsCompletes[dayIndex] = true;
        }
    }

    /// Complete daily challenge from different challengers
    /// @dev complete multiple daily challenges. Only group agent can run.
    /// @param _groupId group id
    /// @param _whos challenger address array
    /// @param timestamps timestamp array to record challenge.dayIsCompletes
    function agentCompleteDailyChallenges(uint256 _groupId, address[] memory _whos, uint256[] memory timestamps) public {
        require(_whos.length == timestamps.length, "must be the same size of _whos and timestamps");
        ChallengeGroup memory group = _getChallengeGroup(_groupId);
        require(msg.sender == group.agent, "permission: agent");

        for (uint256 index = 0; index < _whos.length; index++) {
            (, Challenge storage challenge) = _getCurrentChallenge(_groupId, _whos[index]);

            uint256 timestamp = timestamps[index];
            uint256 dayIndex = timestamp.sub(challenge.startTimestamp) / 1 days;
            if (challenge.dayIsCompletes[dayIndex] == true) {
                continue;
            }
            require(timestamp < challenge.startTimestamp.add(challenge.totalDays * 1 days), "must less than endDay");
            require(timestamp <= now, "must less than or equal to current timestamp");

            uint256 fromTimestamp = now.sub(group.maxDelayDays * 1 days);
            require(timestamp >= fromTimestamp, "must greater than or equal to now - maxDelayDays");

            challenge.completeDays++;
            challenge.dayIsCompletes[dayIndex] = true;
        }
    }

    /// Finish challenge
    /// @dev only challenger can finish succeed challenge. Anyone can finish failed challenge for bonus.
    /// @param _groupId group id
    /// @param _who challenger address
    function finishChallenge(uint256 _groupId, address payable _who) public {
        (uint256 currentRound, Challenge storage challenge) = _getCurrentChallenge(_groupId, _who);

        ChallengeStatus status;
        if (challenge.completeDays >= challenge.targetDays) {
            require(msg.sender == _who, "permission: must be challenger himself");

            // share 10% * 2
            uint256 sharingAmount = challenge.totalSponsorAmount / 10;
            if (sharingAmount > 0) {
                // to group
                ChallengeGroup storage group = challengeGroups[challenge.groupId];
                group.balance = group.balance.add(sharingAmount);

                // to owner
                ownerBalance = ownerBalance.add(sharingAmount);
            }

            // challenger's reward
            uint256 amount = challenge.amount.add(challenge.totalSponsorAmount - sharingAmount * 2);

            // go to next round
            currentRounds[_groupId][_who]++;

            _who.transfer(amount);
            status = ChallengeStatus.Succeeded;
        } else {
            // check endTimestamp
            uint256 endTimestamp = challenge.startTimestamp.add(challenge.totalDays * 1 days);
            require(now >= endTimestamp, "must greater than or equal to endTimestamp");

            if (msg.sender != _who) {
                ChallengeGroup memory group = _getChallengeGroup(_groupId);
                require(now >= endTimestamp.add(group.maxDelayDays * 1 days), "must greater than or equal to endDay + maxDelayDays");
            }

            _handleFailedChallenge(_who, currentRound, challenge);
            status = ChallengeStatus.Failed;
        }

        emit FinishChallenge(
            challenge.groupId,
            _who,
            currentRound,
            challenge.goal,
            challenge.targetDays,
            challenge.totalDays,
            challenge.completeDays,
            challenge.startTimestamp,
            challenge.amount,
            challenge.sponsorSize,
            challenge.totalSponsorAmount,
            uint8(status));
    }

    /// Abort challenge
    /// @dev only challenger can give up his/her challenge
    /// @param _groupId group id
    function abortChallenge(uint256 _groupId) public {
        (uint256 currentRound, Challenge storage challenge) = _getCurrentChallenge(_groupId, msg.sender);
        _handleFailedChallenge(msg.sender, currentRound, challenge);

        emit FinishChallenge(
            challenge.groupId,
            msg.sender,            
            currentRound,
            challenge.goal,
            challenge.targetDays,
            challenge.totalDays,
            challenge.completeDays,
            challenge.startTimestamp,
            challenge.amount,
            challenge.sponsorSize,
            challenge.totalSponsorAmount,
            uint8(ChallengeStatus.Aborted));
    }

    /// Determine if it's be able to sponsor challenge or not
    /// @dev
    /// @param _groupId group id
    /// @param _who challenger address
    /// @return can sponsor or not
    function canSponsor(uint256 _groupId, address _who) public view returns (bool _can) {
        (, Challenge memory challenge) = _getCurrentChallenge(_groupId, _who);
        uint256 day = now.sub(challenge.startTimestamp) / 1 days;
        if (day >= challenge.completeDays) {
            uint256 failedDays = day - challenge.completeDays;
            if (failedDays > challenge.totalDays - challenge.targetDays) {
                return false;
            }
        }

        return true;
    }

    /// Sponsor challenge
    /// @dev sponsor specific one challenge
    /// @param _groupId group id
    /// @param _who challenger address
    /// @param _comment message to encourage challenger
    function sponsorChallenge(uint256 _groupId, address _who, string memory _comment) public payable {
        require(msg.value >= _getChallengeGroup(_groupId).minAmount, "must greater than or equal to minAmount");
        require(canSponsor(_groupId, _who), "can NOT sponsor now");

        (uint256 currentRound, Challenge storage challenge) = _getCurrentChallenge(_groupId, _who);

        bool replace = false;
        uint256 storeIndex = 0;
        if (challenge.sponsorSize > 2) {
            uint256 minAmount = msg.value;
            for (int256 index = 2; index >= 0; index--) {
                if (minAmount > challenge.sponsors[uint256(index)].amount) {
                    replace = true;
                    storeIndex = uint256(index);
                    minAmount = challenge.sponsors[storeIndex].amount;
                }
            }
        }

        if (replace) {
            challenge.sponsors[challenge.sponsorSize] = challenge.sponsors[storeIndex];
            challenge.sponsors[storeIndex] = Sponsor(msg.sender, msg.value, _comment);
        } else {
            challenge.sponsors[challenge.sponsorSize] = Sponsor(msg.sender, msg.value, _comment);
        }

        challenge.sponsorSize++;
        challenge.totalSponsorAmount = challenge.totalSponsorAmount.add(msg.value);

        emit SponsorChallenge(msg.sender, _groupId, _who, currentRound, msg.value, _comment);
    }

    /// Contract owner withdraw
    /// @dev withdraw the balance of owner. Only contract owner can withdraw.
    function ownerWithdraw() public onlyOwner {
        uint256 amount = ownerBalance;
        ownerBalance = 0;
        msg.sender.transfer(amount);
    }

    /// Group owner withdraw
    /// @dev withdraw the balance of challenge group. Only group owner can withdraw.
    function groupWithdraw(uint256 _groupId) public {
        ChallengeGroup storage group = challengeGroups[_groupId];
        require(msg.sender == group.owner, "permission: group owner");
        require(group.balance > 0, "not enough group.balance");
        uint256 amount = group.balance;
        group.balance = 0;
        msg.sender.transfer(amount);
    }

    /// User withdraw
    /// @dev withdraw the balance of user.
    function userWithdraw() public {
        require(userBalances[msg.sender] > 0, "not enough userBalances for msg.sender");
        uint256 amount = userBalances[msg.sender];
        userBalances[msg.sender] = 0;
        msg.sender.transfer(amount);
    }

    /// Get challenge group
    /// @dev get challenge group if exists
    /// @param _id group id
    /// @return ChallengeGroup
    function _getChallengeGroup(uint256 _id) private view returns (ChallengeGroup memory) {
        ChallengeGroup memory group = challengeGroups[_id];
        require(group.minDays > 0, "no group");

        return group;
    }

    /// Get current challenge
    /// @dev get in-progess challenge if exists
    /// @param _groupId group id
    /// @param _challengeOwner challenger address
    /// @return current round number and Challenge
    function _getCurrentChallenge(uint256 _groupId, address _challengeOwner) private view returns (
        uint256 _currentRound,
        Challenge storage _challenge
    ) {
        uint256 currentRound = currentRounds[_groupId][_challengeOwner];
        Challenge storage challenge = challenges[_groupId][_challengeOwner][currentRound];
        require(challenge.totalDays > 0, "no challenge");

        return (currentRound, challenge);
    }

    /// Handle failed challenge
    /// @dev share challenger's amount & 4~N sponsors' amount to:
    /// 10% to caller, will transfer back directly if caller is challenger
    /// 25% to group owner
    /// 40% to the top 3 sponsors (and 100% refund for their sponsors)
    /// 25% to contract owner
    /// @param who challenger address
    /// @param currentRound current round number
    /// @param challenge current Challenge
    function _handleFailedChallenge(address payable who, uint256 currentRound, Challenge storage challenge) private {
        ChallengeGroup storage group = challengeGroups[challenge.groupId];

        uint256 topSponsorsSize = challenge.sponsorSize > 2 ? 3 : challenge.sponsorSize;

        uint256 totalSponsorAmount = challenge.totalSponsorAmount;
        // add the refund
        for (uint256 index = 0; index < topSponsorsSize; index++) {
            Sponsor memory sponsor = challenge.sponsors[index];
            userBalances[sponsor.who] = userBalances[sponsor.who].add(sponsor.amount);
            totalSponsorAmount = totalSponsorAmount.sub(sponsor.amount);
        }

        // share challengers' balance + 4 ~ N sponsors' amount
        uint256 sharingAmount = challenge.amount.add(totalSponsorAmount);

        // 10% of to caller
        uint256 toCallerAmount = sharingAmount / 10;
        // execute transfer in the end of this function

        // 25% to the balance of group owner
        uint256 toGroupOwnerAmount = sharingAmount / 4;
        group.balance = group.balance.add(toGroupOwnerAmount);

        // variable to record current sharing balance
        uint256 toOwnerAmount = sharingAmount.sub(toCallerAmount).sub(toGroupOwnerAmount);

        // 40% to top sponsors
        if (topSponsorsSize > 0) {
            uint256 toEachSponsorAmount = sharingAmount / 5 * 2 / topSponsorsSize;

            for (uint256 index = 0; index < topSponsorsSize; index++) {
                Sponsor memory sponsor = challenge.sponsors[index];
                userBalances[sponsor.who] = userBalances[sponsor.who].add(toEachSponsorAmount);
                toOwnerAmount = toOwnerAmount.sub(toEachSponsorAmount);
                emit SponsorWin(sponsor.who, challenge.groupId, challenge.who, currentRound, toEachSponsorAmount);
            }
        }

        // 25% to contract owner
        ownerBalance = ownerBalance.add(toOwnerAmount);

        // go to next round
        currentRounds[challenge.groupId][who]++;

        msg.sender.transfer(toCallerAmount);
    }
}

