pragma solidity ^0.5.0;

import "openzeppelin-solidity/contracts/ownership/Ownable.sol";
import "openzeppelin-solidity/contracts/math/SafeMath.sol";

contract CoinChallenges is Ownable {
    using SafeMath for uint256;

    uint8 public constant MinDays = 12;
    uint8 public constant MaxDays = 90;

    uint256 private constant DayTimestamp = 86400; // 60 * 60 * 24

    uint256 public ownerBalance;

    struct ChallengeGroup {
        string id;
        string name;
        string url;
        uint8 minDays;
        uint8 maxDays;
        uint8 maxDelayDays;
        uint256 minAmount;
        uint256 balance;
        address owner;
        address agent;
    }

    enum ChallengeStatus { Succeeded, Failed, Aborted, InProgress }

    struct Challenge {
        address who;
        string groupId;
        string goal;
        uint8 targetDays;
        uint8 totalDays;
        uint8 completeDays;
        uint256 startTimestamp;
        uint256 amount;
        uint256 sponsorSize;
        ChallengeStatus status;
        mapping (uint256 => Sponsor) sponsors;
        mapping (uint256 => bool) dayIsCompletes; // index is from 0 to (totalDays - 1)
    }

    struct Sponsor {
        address who;
        uint256 amount;
        string comment;
    }

    // id to ChallengeGroup
    mapping (string => ChallengeGroup) challengeGroups;

    // groupId to (address to Challenge in progress)
    mapping (string => mapping (address => Challenge)) challenges;

    // address to user's balance from sponsor and the profit-sharing of failed challenge
    mapping (address => uint256) public userBalances;

    event NewChallengeGroup(address indexed proposer, string id);
    event NewChallenge(
        address indexed proposer,
        string groupId,
        string goal,
        uint8 targetDays,
        uint8 totalDays,
        uint256 startTimestamp,
        uint256 amount);
    event FinishChallenge(
        address indexed who,
        string groupId,
        string goal,
        uint8 targetDays,
        uint8 totalDays,
        uint8 completeDays,
        uint256 startTimestamp,
        uint256 amount,
        uint256 sponsorSize,
        uint8 indexed status);
    event SponsorChallenge(address indexed who, address indexed challenger, uint256 amount, string comment);
    event SponsorWin(address indexed who, address indexed challenger, string groupId, uint256 amount);

    constructor() public {
    }

    modifier onlyChallengeOwnerAndGroupAgent(string memory _groupId, address _who) {
        ChallengeGroup memory group = _getChallengeGroup(_groupId);
        require(_who == msg.sender || _who == group.agent, "only user and agent can work");
        _;
    }

    function createChallengeGroup(
        string memory _id,
        string memory _name,
        string memory _url,
        uint8 _minDays,
        uint8 _maxDays,
        uint8 _maxDelayDays,
        uint256 _minAmount,
        address _agent
    ) public {
        require(challengeGroups[_id].minDays == 0, "can NOT be the same id of ChallengeGroup");
        require(_minDays >= MinDays, "must greater than global minDays");
        require(_maxDays <= MaxDays, "must less than global maxDays");
        require(_maxDelayDays <= MaxDays, "must less than global maxDays");

        challengeGroups[_id] = ChallengeGroup(_id, _name, _url, _minDays, _maxDays, _maxDelayDays, _minAmount, 0, msg.sender, _agent);
        emit NewChallengeGroup(msg.sender, _id);
    }

    function getChallengeGroup(string memory _id) public view returns (
        string memory _name,
        string memory _url,
        uint8 _minDays,
        uint8 _maxDays,
        uint8 _maxDelayDays,
        uint256 _minAmount,
        uint256 _balance,
        address _owner,
        address _agent
    ) {
        ChallengeGroup memory group = _getChallengeGroup(_id);

        _name = group.name;
        _url = group.url;
        _minDays = group.minDays;
        _maxDays = group.maxDays;
        _maxDelayDays = group.maxDelayDays;
        _minAmount = group.minAmount;
        _balance = group.balance;
        _owner = group.owner;
        _agent = group.agent;
    }

    function modifyChallengeGroup(string memory _id, string memory _name, string memory _url) public {
        ChallengeGroup storage group = challengeGroups[_id];
        require(group.owner == msg.sender, "permission: group owner");

        group.name = _name;
        group.url = _url;
    }

    function createChallenge(
        string memory _groupId,
        string memory _goal,
        uint8 _targetDays,
        uint8 _totalDays,
        uint256 _startTimestamp
    ) public payable {
        require(_startTimestamp > now - 1 days, "must greater than now - 1 day");
        ChallengeGroup memory group = _getChallengeGroup(_groupId);

        require(challenges[_groupId][msg.sender].totalDays == 0, "can NOT create duplicate challenge");
        require(_totalDays >= group.minDays, "must greater than or equal to group's minDays");
        require(_totalDays <= group.maxDays, "must less than or equal to group's maxDays");
        require(_targetDays >= group.minDays, "must greater than or equal to group's minDays");
        require(_targetDays <= _totalDays, "must less than or equal to totalDays");
        require(msg.value >= group.minAmount, "must greater than group's minAmount");

        challenges[_groupId][msg.sender] = Challenge(msg.sender, _groupId, _goal, _targetDays, _totalDays, 0, _startTimestamp, msg.value, 0, ChallengeStatus.InProgress);
        for (uint dayIndex = 0; dayIndex < _totalDays; dayIndex++) {
          if (challenges[_groupId][msg.sender].dayIsCompletes[dayIndex] == true) {
            delete challenges[_groupId][msg.sender].dayIsCompletes[dayIndex];
          }
        }
        emit NewChallenge(msg.sender, _groupId, _goal, _targetDays, _totalDays, _startTimestamp, msg.value);
    }

    function getChallenge(
        string memory _groupId,
        address _address
    ) public view returns (
        string memory _goal,
        uint8 _targetDays,
        uint8 _totalDays,
        uint8 _completeDays,
        uint256 _startTimestamp,
        uint256 _amount,
        uint256 _sponsorSize,
        uint256[] memory _completeTimestamps
    ) {
        Challenge storage challenge = challenges[_groupId][_address];

        _completeTimestamps = new uint256[](challenge.completeDays);
        uint8 index = 0;
        for (uint dayIndex = 0; dayIndex < challenge.totalDays; dayIndex++) {
          if (challenge.dayIsCompletes[dayIndex] == true) {
            _completeTimestamps[index] = challenge.startTimestamp.add(dayIndex * DayTimestamp);
            index++;
          }
        }

        _goal = challenge.goal;
        _targetDays = challenge.targetDays;
        _totalDays = challenge.totalDays;
        _completeDays = challenge.completeDays;
        _startTimestamp = challenge.startTimestamp;
        _amount = challenge.amount;
        _sponsorSize = challenge.sponsorSize;
    }

    function getSponsor(
        string memory _groupId,
        address _address,
        uint256 _index
    ) public view returns (
        address _who,
        uint256 _amount,
        string memory _comment
    ) {
        require(_index < challenges[_groupId][_address].sponsorSize, "must less than sponsor size");
        Sponsor memory sponsor = challenges[_groupId][_address].sponsors[_index];
        return (sponsor.who, sponsor.amount, sponsor.comment);
    }

    /**
     * @dev complete multiple daily challenges. Only challenger and group agent can run.
     */
    function completeDailyChallenges(string memory _groupId, address _who, uint256[] memory timestamps)
        public onlyChallengeOwnerAndGroupAgent(_groupId, _who) {

        ChallengeGroup memory group = _getChallengeGroup(_groupId);
        Challenge storage challenge = _getChallenge(_groupId, msg.sender);

        for (uint256 index = 0; index < timestamps.length; index++) {
            uint256 timestamp = timestamps[index];
            uint256 dayIndex = timestamp.sub(challenge.startTimestamp).div(DayTimestamp);
            if (challenge.dayIsCompletes[dayIndex] == true) {
                continue;
            }
            require(timestamp < challenge.startTimestamp.add(challenge.totalDays * DayTimestamp), "must less than challenge's endDay");
            require(timestamp <= now, "must less than current timestamp");

            uint256 fromTimestamp = now.sub(group.maxDelayDays * DayTimestamp);
            require(timestamp >= fromTimestamp, "must greater than now - challenge.maxDelayDays");

            challenge.completeDays++;
            challenge.dayIsCompletes[dayIndex] = true;
        }
    }

    /**
     * @dev finish challenge. Anyone can help finish any challenge.
     */
    function finishChallenge(string memory _groupId, address payable _who) public {
        Challenge storage challenge = _getChallenge(_groupId, _who);
        require(now >= challenge.startTimestamp.add(challenge.totalDays * DayTimestamp), "must greater than challenge's EndDay");
        require(challenge.status == ChallengeStatus.InProgress, "challenge status is NOT InProgress");

        if (challenge.completeDays >= challenge.targetDays) {
            challenge.status = ChallengeStatus.Succeeded;

            // set challenger's amount
            uint256 amount = challenge.amount;

            // add all sponsors' amount to amount
            for (uint i = 0; i < challenge.sponsorSize; i++) {
                amount = amount.add(challenge.sponsors[i].amount);
            }

            _who.transfer(amount);
        } else {
            ChallengeGroup memory group = _getChallengeGroup(_groupId);
            require(now >= challenge.startTimestamp.add((challenge.totalDays + group.maxDelayDays) * DayTimestamp), "must greater than challenge's EndDay + group's maxDelayDays");

            challenge.status = ChallengeStatus.Failed;
            _handleFailedChallenge(_who, challenge);
        }

        emit FinishChallenge(
            _who,
            challenge.groupId,
            challenge.goal,
            challenge.targetDays,
            challenge.totalDays,
            challenge.completeDays,
            challenge.startTimestamp,
            challenge.amount,
            challenge.sponsorSize,
            uint8(challenge.status));
        delete challenges[_groupId][_who];
    }

    /**
     * @dev abort challenge. Only yourself can give up the challenge.
     */
    function abortChallenge(string memory _groupId) public {
        Challenge storage challenge = _getChallenge(_groupId, msg.sender);
        require(challenge.status == ChallengeStatus.InProgress, "challenge status is NOT InProgress");
        challenge.status = ChallengeStatus.Aborted;
        _handleFailedChallenge(msg.sender, challenge);

        emit FinishChallenge(
            msg.sender,
            challenge.groupId,
            challenge.goal,
            challenge.targetDays,
            challenge.totalDays,
            challenge.completeDays,
            challenge.startTimestamp,
            challenge.amount,
            challenge.sponsorSize,
            uint8(challenge.status));
        delete challenges[_groupId][msg.sender];
    }

    function canSponsor(string memory _groupId, address _who) public view returns (bool _can) {
        Challenge memory challenge = _getChallenge(_groupId, _who);
        uint256 day = 1 + now.sub(challenge.startTimestamp).div(DayTimestamp);
        uint256 failedDays = day - challenge.completeDays;
        if (failedDays > challenge.totalDays - challenge.targetDays) {
            return false;
        }

        return true;
    }

    /**
     * @dev sponsor specific one challenge.
     */
    function sponsorChallenge(string memory _groupId, address _who, string memory comment) public payable {
        require(msg.value >= _getChallengeGroup(_groupId).minAmount, "sponsor value must greater than group.minAmount");
        require(canSponsor(_groupId, _who), "can NOT sponsor");

        Challenge storage challenge = _getChallenge(_groupId, _who);
        challenge.sponsors[challenge.sponsorSize] = Sponsor(msg.sender, msg.value, comment);
        challenge.sponsorSize++;

        emit SponsorChallenge(msg.sender, _who, msg.value, comment);
    }

    /**
     * @dev withdraw the balance of owner. Only contract owner can withdraw.
     */
    function ownerWithdraw() public onlyOwner {
        uint256 amount = ownerBalance;
        ownerBalance = 0;
        msg.sender.transfer(amount);
    }

    /**
     * @dev withdraw the balance of challenge group. Only group owner can withdraw.
     */
    function groupWithdraw(string memory _groupId) public {
        ChallengeGroup storage group = challengeGroups[_groupId];
        require(msg.sender == group.owner, "permission: group owner");
        require(group.balance > 0, "not enough group.balance");
        uint256 amount = group.balance;
        group.balance = 0;
        msg.sender.transfer(amount);
    }

    /**
     * @dev withdraw the balance of user.
     */
    function userWithdraw() public {
        require(userBalances[msg.sender] > 0, "not enough userBalances for msg.sender");
        uint256 amount = userBalances[msg.sender];
        userBalances[msg.sender] = 0;
        msg.sender.transfer(amount);
    }

    /**
     * @dev get challenge group if exists
     */
    function _getChallengeGroup(string memory _id) private view returns (ChallengeGroup memory) {
        ChallengeGroup memory group = challengeGroups[_id];
        require(group.minDays > 0, "group doesn't exist");

        return group;
    }

    /**
     * @dev get in-progess challenge if exists
     */
    function _getChallenge(string memory _groupId, address _challengeOwner) private view returns (Challenge storage) {
        Challenge storage challenge = challenges[_groupId][_challengeOwner];
        require(challenge.totalDays > 0, "challenge doesn't exist");

        return challenge;
    }

    /**
     * @dev transfer money to the below:
     * 10% of challenger's amount back to original challenger
     * 40% of challenger's amount to sponsors (and 100% refund to sponsors)
     * 25% of challenger's amount to contract owner
     * 25% of challenger's amount to challenge group owner
     */
    function _handleFailedChallenge(address payable who, Challenge storage challenge) private {
        ChallengeGroup storage group = challengeGroups[challenge.groupId];

        // transfer 10% back to challenger
        uint256 toChallengerAmount = challenge.amount.div(10);
        // execute transfer in the end of this function

        // add the refund to the balances of sponsors
        uint256 allSponsorsAmount = 0;
        for (uint256 index = 0; index < challenge.sponsorSize; index++) {
            Sponsor memory sponsor = challenge.sponsors[index];
            userBalances[sponsor.who] = userBalances[sponsor.who].add(sponsor.amount);
            allSponsorsAmount = allSponsorsAmount.add(sponsor.amount);
        }

        // transfer 40% to sponsors
        uint256 toSponsorsAmount = challenge.amount.div(10).mul(4);
        uint256 realToSponsorsAmount = 0;
        for (uint256 index = 0; index < challenge.sponsorSize; index++) {
            Sponsor memory sponsor = challenge.sponsors[index];
            uint256 amount = toSponsorsAmount.mul(sponsor.amount).div(allSponsorsAmount);
            userBalances[sponsor.who] = userBalances[sponsor.who].add(amount);
            realToSponsorsAmount = realToSponsorsAmount.add(amount);
            emit SponsorWin(sponsor.who, challenge.who, challenge.groupId, amount);
        }

        // add amount to the balance of group owner
        uint256 toGroupOwnerAmount = challenge.amount.div(4);
        group.balance = group.balance.add(toGroupOwnerAmount);

        uint256 toOwnerAmount = challenge.amount.sub(toChallengerAmount).sub(realToSponsorsAmount).sub(toGroupOwnerAmount);
        // add amount to the balance of contract owner
        ownerBalance = ownerBalance.add(toOwnerAmount);

        who.transfer(toChallengerAmount);
    }
}
