pragma solidity ^0.4.24;

import "openzeppelin-solidity/contracts/ownership/Ownable.sol";
import "openzeppelin-solidity/contracts/math/SafeMath.sol";

contract CoinChallenges is Ownable {
    using SafeMath for uint256;

    uint8 public constant MinDays = 12;
    uint8 public constant MaxDays = 90;

    uint256 private constant DayTimestamp = 86400; // 60 * 60 * 24

    uint256 public ownerBalance;

    struct ChallengeGroup {
        string id;
        string name;
        string url;
        uint8 minDays;
        uint8 maxDays;
        uint8 maxDelayDays;
        uint256 minAmount;
        uint256 balance;
        address owner;
        address agent;
    }

    enum ChallengeStatus { Succeeded, Failed, Aborted }

    struct Challenge {
        address who;
        string groupId;
        uint8 targetDays;
        uint8 totalDays;
        uint8 completeDays;
        uint256 startDayTimestamp; // timestamp / dayTimestamp
        uint256 amount;
        uint256 sponsorSize;
        mapping (uint256 => Sponsor) sponsors;
        mapping (uint256 => bool) dayTimestampIsCompletes; // timestamp / dayTimestamp
    }

    struct Sponsor {
        address who;
        uint256 amount;
        string comment;
    }

    // id to ChallengeGroup
    mapping (string => ChallengeGroup) challengeGroups;

    // groupId to succeeded Challenges
    mapping (string => Challenge[]) succeededChallenges;

    // the start index of succeeded challenges
    uint256 public succeededChallengesStartIndex;

    // groupId to (address to Challenge in progress)
    mapping (string => mapping (address => Challenge)) challenges;

    // address to user's balance from sponsor and the profit-sharing of failed challenge
    mapping (address => uint256) userBalances;

    event NewChallengeGroup(address indexed proposer, string id);
    event NewChallenge(address indexed proposer, string groupId, uint8 targetDays, uint8 totalDays, uint256 startDayTimestamp, uint256 amount);
    event FinishChallenge(address indexed who, uint8 indexed status, uint8 targetDays, uint8 totalDays, uint8 completeDays, uint256 amount);
    event ProfitSharing(address indexed who, address indexed challenger, string groupId, uint256 amount);
    event SponsorChallenge(address indexed who, address indexed challenger, uint256 amount, string comment);

    constructor() public {
    }

    modifier onlyChallengeOwnerAndGroupAgent(string _groupId, address _who) {
        ChallengeGroup memory group = _getChallengeGroup(_groupId);
        require(_who == msg.sender || _who == group.agent, "only user and agent can work");
        _;
    }

    function createChallengeGroup(
        string _id,
        string _name,
        string _url,
        uint8 _minDays,
        uint8 _maxDays,
        uint8 _maxDelayDays,
        uint256 _minAmount,
        address _agent
    ) public {
        require(challengeGroups[_id].minDays == 0, "can NOT be the same id of ChallengeGroup");
        require(_minDays >= MinDays, "must greater than global minDays");
        require(_maxDays <= MaxDays, "must less than global maxDays");
        require(_maxDelayDays <= MaxDays, "must less than global maxDays");

        challengeGroups[_id] = ChallengeGroup(_id, _name, _url, _minDays, _maxDays, _maxDelayDays, _minAmount, 0, msg.sender, _agent);
        emit NewChallengeGroup(msg.sender, _id);
    }

    function getChallengeGroup(string _id) public view returns (
        string _name,
        string _url,
        uint8 _minDays,
        uint8 _maxDays,
        uint8 _maxDelayDays,
        uint256 _minAmount,
        uint256 _balance,
        address _owner,
        address _agent
    ) {
        ChallengeGroup memory group = _getChallengeGroup(_id);

        _name = group.name;
        _url = group.url;
        _minDays = group.minDays;
        _maxDays = group.maxDays;
        _maxDelayDays = group.maxDelayDays;
        _minAmount = group.minAmount;
        _balance = group.balance;
        _owner = group.owner;
        _agent = group.agent;
    }

    function modifyChallengeGroup(string _id, string _name, string _url) public {
        ChallengeGroup storage group = challengeGroups[_id];
        require(group.owner == msg.sender, "must be the owner of group");

        group.name = _name;
        group.url = _url;
    }

    function createChallenge(
        string _groupId,
        uint8 _targetDays,
        uint8 _totalDays,
        uint256 _startTimestamp
    ) public payable {
        ChallengeGroup memory group = _getChallengeGroup(_groupId);

        require(challenges[_groupId][msg.sender].totalDays == 0, "can NOT create duplicate challenge");
        require(_totalDays >= group.minDays, "must greater than or equal to group's minDays");
        require(_totalDays <= group.maxDays, "must less than or equal to group's maxDays");
        require(_targetDays >= group.minDays, "must greater than or equal to group's minDays");
        require(_targetDays <= _totalDays, "must less than or equal to totalDays");
        require(msg.value >= group.minAmount, "must greater than group's minAmount");

        challenges[_groupId][msg.sender] = Challenge(msg.sender, _groupId, _targetDays, _totalDays, 0, _getDayTimestamp(_startTimestamp), msg.value, 0);

        emit NewChallenge(msg.sender, _groupId, _targetDays, _totalDays, _startTimestamp, msg.value);
    }

    function getChallenge(
        string _groupId,
        address _address
    ) public view returns (
        uint8 _targetDays,
        uint8 _totalDays,
        uint8 _completeDays,
        uint256 _startDayTimestamp,
        uint256 _amount,
        uint256 _sponsorSize
    ) {
        Challenge memory challenge = challenges[_groupId][_address];

        _targetDays = challenge.targetDays;
        _totalDays = challenge.totalDays;
        _completeDays = challenge.completeDays;
        _startDayTimestamp = challenge.startDayTimestamp;
        _amount = challenge.amount;
        _sponsorSize = challenge.sponsorSize;
    }

    function getSponsor(
        string _groupId,
        address _address,
        uint256 _index
    ) public view returns (
        address _who,
        uint256 _amount,
        string _comment
    ) {
        Sponsor memory sponsor = challenges[_groupId][_address].sponsors[_index];
        return (sponsor.who, sponsor.amount, sponsor.comment);
    }

    /**
     * @dev complete multiple daily challenges. Only challenger and group agent can run.
     */
    function completeDailyChallenges(string _groupId, address _who, uint256[] timestamps)
        public onlyChallengeOwnerAndGroupAgent(_groupId, _who) {

        ChallengeGroup memory group = _getChallengeGroup(_groupId);
        Challenge storage challenge = _getChallenge(_groupId, msg.sender);

        for (uint256 index = 0; index < timestamps.length; index++) {
            uint256 dayTimestamp = _getDayTimestamp(timestamps[index]);
            if (challenge.dayTimestampIsCompletes[dayTimestamp] == true) {
                continue;
            }
            require(dayTimestamp >= challenge.startDayTimestamp, "must greater than challenge's startDayTimestamp");
            require(dayTimestamp < challenge.startDayTimestamp.add(challenge.totalDays), "must less than challenge's endDay");

            uint256 nowDayTimestamp = _getDayTimestamp(now);
            require(dayTimestamp <= nowDayTimestamp, "must less than now's dayTimestamp");

            uint256 fromDayTimestamp = nowDayTimestamp.sub(group.maxDelayDays);
            require(dayTimestamp >= fromDayTimestamp, "must greater than now - challenge.maxDelayDays");

            challenge.completeDays++;
            challenge.dayTimestampIsCompletes[dayTimestamp] = true;
        }
    }

    /**
     * @dev finish challenge. Anyone can help finish any challenge.
     */
    function finishChallenge(string _groupId, address _who) public {
        Challenge storage challenge = _getChallenge(_groupId, _who);
        uint256 nowDayTimestamp = _getDayTimestamp(now);
        require(nowDayTimestamp >= challenge.startDayTimestamp.add(challenge.totalDays), "must greater than challenge's EndDay");

        ChallengeStatus status;
        if (challenge.completeDays >= challenge.targetDays) {
            status = ChallengeStatus.Succeeded;

            // set challenger's amount
            uint256 amount = challenge.amount;

            // add all sponsors' amount to amount
            for (uint i = 0; i < challenge.sponsorSize; i++) {
                amount = amount.add(challenge.sponsors[i].amount);
            }

            _who.transfer(amount);

            succeededChallenges[_groupId].push(challenge);
        } else {
            ChallengeGroup memory group = _getChallengeGroup(_groupId);
            require(nowDayTimestamp >= challenge.startDayTimestamp.add(challenge.totalDays).add(group.maxDelayDays), "must greater than challenge's EndDay + group's maxDelayDays");

            status = ChallengeStatus.Failed;
            _handleFailedChallenge(_who, challenge);
        }

        emit FinishChallenge(_who, uint8(status), challenge.targetDays, challenge.totalDays, challenge.completeDays, challenge.amount);
        delete challenges[_groupId][_who];
    }

    /**
     * @dev abort challenge. Only yourself can give up the challenge.
     */
    function abortChallenge(string _groupId) public {
        Challenge storage challenge = _getChallenge(_groupId, msg.sender);
        _handleFailedChallenge(msg.sender, challenge);

        emit FinishChallenge(msg.sender, uint8(ChallengeStatus.Aborted), challenge.targetDays, challenge.totalDays, challenge.completeDays, challenge.amount);
        delete challenges[_groupId][msg.sender];
    }

    /**
     * @dev sponsor specific one challenge.
     */
    function sponsorChallenge(string _groupId, address _who, string comment) public payable {
        require(msg.value >= _getChallengeGroup(_groupId).minAmount, "sponsor value must greater than group.minAmount");

        Challenge storage challenge = _getChallenge(_groupId, _who);
        challenge.sponsors[challenge.sponsorSize] = Sponsor(msg.sender, msg.value, comment);
        challenge.sponsorSize++;

        emit SponsorChallenge(msg.sender, _who, msg.value, comment);
    }

    /**
     * @dev withdraw the balance of owner. Only contract owner can withdraw.
     */
    function ownerWithdraw() public onlyOwner {
        msg.sender.transfer(ownerBalance);
        ownerBalance = 0;
    }

    /**
     * @dev withdraw the balance of challenge group. Only group owner can withdraw.
     */
    function groupWithdraw(string _groupId) public {
        ChallengeGroup storage group = challengeGroups[_groupId];
        require(msg.sender == group.owner, "only group owner can work");
        msg.sender.transfer(group.balance);
        group.balance = 0;
    }

    /**
     * @dev withdraw the balance of user.
     */
    function userWithdraw() public {
        require(userBalances[msg.sender] > 0, "must greater than 0");

        msg.sender.transfer(userBalances[msg.sender]);
        userBalances[msg.sender] = 0;
    }

    /**
     * @dev compute dayTimestamp from timestamp.
     * @param _timestamp timestamp
     * @return dayTimestamp
     */
    function _getDayTimestamp(uint256 _timestamp) private pure returns (uint256) {
        return _timestamp / DayTimestamp;
    }

    /**
     * @dev get challenge group if exists
     */
    function _getChallengeGroup(string _id) private view returns (ChallengeGroup) {
        ChallengeGroup memory group = challengeGroups[_id];
        require(group.minDays > 0, "group doesn't exist");

        return group;
    }

    /**
     * @dev get in-progess challenge if exists
     */
    function _getChallenge(string _groupId, address _challengeOwner) private view returns (Challenge storage) {
        Challenge storage challenge = challenges[_groupId][_challengeOwner];
        require(challenge.totalDays > 0, "challenge doesn't exist");

        return challenge;
    }

    /**
     * @dev transfer money to the below:
     * 20% of challenger's amount to the first winner on succeededChallenges
     * 20% of challenger's amount to challenge group owner
     * 10% of challenger's amount back to original challenger
     * 50% of challenger's add to balance of contract owner
     * 100% refund to sponsors
     */
    function _handleFailedChallenge(address who, Challenge storage challenge) private {
        ChallengeGroup storage group = challengeGroups[challenge.groupId];

        // transfer to the first winner whose amount is greater than challenger's
        uint256 toFirstWinnerAmount = _addAmountToFirstWinner(challenge);

        // add amount to the balance of group
        uint256 toGroupOwnerAmount = challenge.amount.div(10).mul(2);
        group.balance = group.balance.add(toGroupOwnerAmount);

        // transfer back to challenger
        uint256 toChallengerAmount = challenge.amount.div(10);
        who.transfer(toChallengerAmount);

        // add amount to balance of contract owner
        uint256 toOwnerAmount = challenge.amount.sub(toFirstWinnerAmount).sub(toGroupOwnerAmount).sub(toChallengerAmount);
        ownerBalance = ownerBalance.add(toOwnerAmount);

        // add amount to sponsors' balances
        for (uint256 index = 0; index < challenge.sponsorSize; index++) {
            Sponsor memory sponsor = challenge.sponsors[index];
            userBalances[sponsor.who] = userBalances[sponsor.who].add(sponsor.amount);
        }
    }

    /**
     * @dev add the profit-sharing amount to the first winner whose amount is greater and equal than challenger's
     */
    function _addAmountToFirstWinner(Challenge storage challenge) private returns (uint256) {
        uint256 toFirstWinnerAmount = 0;
        uint256 noZeroAmountIndex = 0;

        Challenge[] memory _challenges = succeededChallenges[challenge.groupId];
        for (uint256 index = succeededChallengesStartIndex; index < _challenges.length; index++) {
            Challenge memory succeededChallenge = _challenges[index];

            if (noZeroAmountIndex == 0 && succeededChallenge.amount > 0) {
                noZeroAmountIndex = index;
            }

            if (succeededChallenge.amount >= challenge.amount) {
                toFirstWinnerAmount = challenge.amount.div(10).mul(2);

                succeededChallenges[challenge.groupId][index].amount = 0;

                userBalances[succeededChallenge.who] = userBalances[succeededChallenge.who].add(toFirstWinnerAmount);
                emit ProfitSharing(succeededChallenge.who, challenge.who, challenge.groupId, toFirstWinnerAmount);

                break;
            }
        }
        if (noZeroAmountIndex != 0) {
            succeededChallengesStartIndex = noZeroAmountIndex;
        }

        return toFirstWinnerAmount;
    }
}

